  42: { text: 'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنتُمْ تَعْلَمُونَ', translation: { en: 'And do not mix the truth with falsehood or conceal the truth while you know.', ur: 'اور حق کو باطل سے نہ ملاؤ اور سچائی کو نہ چھپاؤ جبکہ تم جانتے ہو' }, words: [{ id: '2:42:1', position: 1, arabic: 'وَلَا', transliteration: 'wa-la', pos: ['CONJ', 'NEG'], posLabel: 'CONJ+NEG', meaning: { en: 'And do not', ur: 'اور مت' } }, { id: '2:42:2', position: 2, arabic: 'تَلْبِسُوا', transliteration: 'talbisu', pos: ['V'], posLabel: 'V', root: 'ل ب س', meaning: { en: 'mix', ur: 'ملاؤ' }, grammarRole: 'verb' }, { id: '2:42:3', position: 3, arabic: 'الْحَقَّ', transliteration: 'al-haqqa', pos: ['N'], posLabel: 'N', root: 'ح ق ق', meaning: { en: 'the truth', ur: 'حق کو' }, grammarRole: 'obj', case: 'mansoob' }, { id: '2:42:4', position: 4, arabic: 'بِالْبَاطِلِ', transliteration: 'bil-batili', pos: ['P', 'N'], posLabel: 'P+N', root: 'ب ط ل', meaning: { en: 'with falsehood', ur: 'باطل سے' } }, { id: '2:42:5', position: 5, arabic: 'وَتَكْتُمُوا', transliteration: 'wa-taktumu', pos: ['CONJ', 'V'], posLabel: 'CONJ+V', root: 'ك ت م', meaning: { en: 'or conceal', ur: 'اور چھپاؤ' }, grammarRole: 'verb' }, { id: '2:42:6', position: 6, arabic: 'الْحَقَّ', transliteration: 'al-haqqa', pos: ['N'], posLabel: 'N', root: 'ح ق ق', meaning: { en: 'the truth', ur: 'سچائی کو' }, grammarRole: 'obj' }, { id: '2:42:7', position: 7, arabic: 'وَأَنتُمْ', transliteration: 'wa-antum', pos: ['CONJ', 'PRON'], posLabel: 'CONJ+PRON', meaning: { en: 'while you', ur: 'جبکہ تم' }, grammarRole: 'hal' }, { id: '2:42:8', position: 8, arabic: 'تَعْلَمُونَ', transliteration: 'ta\'lamuna', pos: ['V'], posLabel: 'V', root: 'ع ل م', meaning: { en: 'know', ur: 'جانتے ہو' }, grammarRole: 'verb' }], structure: { relationships: [{ from: 1, to: 2, label: 'نہی + فعل' }, { from: 2, to: 3, label: 'فعل + مفعول به' }] } },
